import React,{useState} from 'react';
import './App.css';

// import components
import VolunteerDash from "./Components/Pages/VolunteerDash";

function App() {
    const [viewMode, setMode] = useState("light");

    const changeMode = () => {
        viewMode === 'light' ? setMode('dark') : setMode('light');
    }

  return (
    <div className="App">
      <VolunteerDash mode = {viewMode}
      changeMode = {changeMode}
      />
    </div>
  );
}

export default App;
